INSERT INTO movie VALUES('1119199', '08KR002', 'Egy ember, aki Superman volt', 'Superman ieotdeon sanai', '1h42min', 'Dráma,Vígjáték', 2008, 'HD', 7.4, 'sl4UUI2Wn_g', 'kr', 'hu,gb', 1);
