INSERT INTO movie VALUES('1059200', '07KR002', 'A kiszeng költőnő élete', 'Hwang Jin-yi', '2h21min', 'Dráma,Életrajz,Történelmi', 2007, 'DVD', 6.5, '922wP3ruEiw', 'kr', 'hu', 1);
INSERT INTO movie_detail VALUES('07KR002', 'Egy arisztokrata családban nevelkedő lányról kiderül, hogy valójában csak fogadott gyerek, valódi anyja egy szolgáló. El kell hagynia a családi házat, s a nem túl rózsás lehetőségei közül a legjobbnak az tűnik, hogy beáll dolgozni egy bordélyházba. Rövid időn belül a legkeresettebb társalkodó hölgy lesz a városban, s feltűnik gyermekkori szerelme is, aki idő közben banditává avanzsált. A helyzetet bonyolítja új helytartó érkezik, aki szintén kiveti hálóját Hwang Jin-yi-re, s úgy tűnik, éppen a megfelelő utat választja a hölgy szívének elnyeréséhez…');
INSERT INTO movie VALUES('1165253', '08KR003', 'Álom', 'Bi-mong', '2h21min', 'Dráma,Fanzázia,Misztikus', 2008, 'DVD', 6.6, 'fV8aAEFfGu4', 'kr', 'hu,gb,ru', 1);
INSERT INTO movie_detail VALUES('08KR003', 'A dél-koreai sztárrendező, Kim Ki-duk 15. nagyjátékfilmjében Jin felriad egy olyan rémálomból, amelyben ő egy közlekedési balesetet okozott. Felzaklatva rögvest autóba ül és elvezet a megálmodott ütközés helyszínére, ahol kiderül, hogy valóban megtörtént az eset. Az elkövető, Ran (Lee Na-young) tagadja, hogy bármi köze lenne a balesethez, állítása szerint ugyanis ő végig aludt az otthonában. Jin rádöbben, hogy a lány alvajáróként ténylegesen véghezvisz mindent, amit ő megálmodik.');
INSERT INTO movie VALUES('2290840', '12KR005', 'A férfi aki királyt játszott', 'Gwanghae: Wangyidoen namja', '2h11min', 'Dráma,Történelmi', 2012, 'HD', 7.8, 'PAZ7wU2P_lI', 'kr', 'hu', 1);
