INSERT INTO movie VALUES('4696222', '15KR007', 'Északi határvonal', 'Yeonpyeong haejeon', '2h10min', 'Dráma,Háborús', 2015, 'HD', 6.6, 'MeoaaZASBNs', 'kr', 'hu,gb', 1);
