INSERT INTO movie VALUES('3501416', '15KR008', 'A bérgyilkosság', 'Amsal', '2h20min', 'Akció,Dráma,Thriller', 2015, 'DVD', 7.3, 'vuLffAVWkXQ', 'kr', 'hu,gb', 1);
